import { ENV } from "../env";

export type Plan = "TRIAL"|"BASIC"|"SMALL"|"MEDIUM"|"LARGE"|"UNLIMITED";

const map: Record<Plan, string | undefined> = {
  TRIAL: ENV.PRICE_TRIAL,
  BASIC: ENV.PRICE_BASIC,
  SMALL: ENV.PRICE_SMALL,
  MEDIUM: ENV.PRICE_MEDIUM,
