import { useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { useToast } from './use-toast';

interface NotificationOptions {
  user: any;
  enabled?: boolean;
}

export function usePassNotifications({ user, enabled = true }: NotificationOptions) {
  const { toast } = useToast();

  // Query active passes for the user's school
  const { data: activePasses = [] } = useQuery({
    queryKey: ['/api/passes/active', user?.schoolId],
    enabled: enabled && !!user?.schoolId,
    refetchInterval: 30000, // Check every 30 seconds
  });

  // Check for passes expiring soon
  useEffect(() => {
    if (!enabled || !activePasses.length) return;

    const now = new Date();
    const expiringSoon = activePasses.filter((pass: any) => {
      const expiresAt = new Date(pass.expiresAt);
      const timeRemaining = expiresAt.getTime() - now.getTime();
      return timeRemaining <= 5 * 60 * 1000 && timeRemaining > 0; // 5 minutes or less
    });

    expiringSoon.forEach((pass: any) => {
      const expiresAt = new Date(pass.expiresAt);
      const minutesRemaining = Math.floor((expiresAt.getTime() - now.getTime()) / (1000 * 60));

      toast({
        title: "Pass Expiring Soon",
        description: `${pass.studentName}'s pass expires in ${minutesRemaining} minute${minutesRemaining !== 1 ? 's' : ''}`,
        variant: "destructive",
      });
    });
  }, [activePasses, enabled, toast]);

  return { activePasses };
}

client/src/hooks/use-safe-state.ts.content:
/**
 * Safe State Management Hooks with Null Protection
 */

import { useState, useCallback, useEffect } from 'react';
import { invariant, withDefault, isNotNull } from '@/lib/null-guards';

/**
 * Safe localStorage hook with null protection
 */
export function useSafeLocalStorage<T>(
  key: string,
  defaultValue: T,
  serializer = JSON
): [T, (value: T) => void, () => void] {
  const [storedValue, setStoredValue] = useState<T>(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? serializer.parse(item) : defaultValue;
    } catch (error) {
      console.warn(`Error reading localStorage key "${key}":`, error);
      return defaultValue;
    }
  });

  const setValue = useCallback((value: T) => {
    try {
      invariant(value !== null && value !== undefined, 'Cannot store null or undefined in localStorage');
      setStoredValue(value);
      window.localStorage.setItem(key, serializer.stringify(value));
    } catch (error) {
      console.error(`Error setting localStorage key "${key}":`, error);
    }
  }, [key, serializer]);

  const removeValue = useCallback(() => {
    try {
      setStoredValue(defaultValue);
      window.localStorage.removeItem(key);
    } catch (error) {
      console.error(`Error removing localStorage key "${key}":`, error);
    }
  }, [key, defaultValue]);

  return [storedValue, setValue, removeValue];
}

/**
 * Safe array state with null protection
 */
export function useSafeArray<T>(initialValue: T[] = []): {
  items: T[];
  addItem: (item: T) => void;
  removeItem: (predicate: (item: T) => boolean) => void;
  updateItem: (predicate: (item: T) => boolean, updater: (item: T) => T) => void;
  clearItems: () => void;
  findItem: (predicate: (item: T) => boolean) => T | undefined;
} {
  const [items, setItems] = useState<T[]>(withDefault(initialValue, []));

  const addItem = useCallback((item: T) => {
    invariant(item, 'Cannot add null or undefined item to array');
    setItems(prev => [...prev, item]);
  }, []);

  const removeItem = useCallback((predicate: (item: T) => boolean) => {
    setItems(prev => prev.filter(item => !predicate(item)));
  }, []);

  const updateItem = useCallback((
    predicate: (item: T) => boolean,
    updater: (item: T) => T
  ) => {
    setItems(prev => prev.map(item => {
      if (predicate(item)) {
        const updated = updater(item);
        invariant(updated, 'Updater function cannot return null or undefined');
        return updated;
      }
      return item;
    }));
  }, []);

  const clearItems = useCallback(() => {
    setItems([]);
  }, []);

  const findItem = useCallback((predicate: (item: T) => boolean) => {
    return items.find(predicate);
  }, [items]);

  return {
    items,
    addItem,
    removeItem,
    updateItem,
    clearItems,
    findItem
  };
}

/**
 * Safe object state with null protection
 */
export function useSafeObject<T extends Record<string, any>>(
  initialValue: T
): [T, (updater: Partial<T> | ((prev: T) => T)) => void, () => void] {
  const [object, setObject] = useState<T>(() => {
    invariant(initialValue, 'Initial value cannot be null or undefined');
    return initialValue;
  });

  const updateObject = useCallback((updater: Partial<T> | ((prev: T) => T)) => {
    setObject(prev => {
      const updated = typeof updater === 'function' 
        ? updater(prev)
        : { ...prev, ...updater };

      invariant(updated, 'Updated object cannot be null or undefined');
      return updated;
    });
  }, []);

  const resetObject = useCallback(() => {
    setObject(initialValue);
  }, [initialValue]);

  return [object, updateObject, resetObject];
}

/**
 * Safe async state with null protection
 */
export function useSafeAsync<T, E = Error>(
  asyncFn: () => Promise<T>,
  deps: React.DependencyList = []
): {
  data: T | null;
  error: E | null;
  loading: boolean;
  execute: () => Promise<void>;
} {
  const [data, setData] = useState<T | null>(null);
  const [error, setError] = useState<E | null>(null);
  const [loading, setLoading] = useState(false);

  const execute = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const result = await asyncFn();
      setData(result);
    } catch (err) {
      setError(err as E);
      setData(null);
    } finally {
      setLoading(false);
    }
  }, deps);

  useEffect(() => {
    execute();
  }, [execute]);

  return { data, error, loading, execute };
}

/**
 * Safe form state with validation and null protection
 */
export function useSafeForm<T extends Record<string, any>>(
  initialValues: T,
  validate?: (values: T) => Record<string, string>
): {
  values: T;
  errors: Record<string, string>;
  touched: Record<string, boolean>;
  setValue: (field: keyof T, value: T[keyof T]) => void;
  setValues: (values: Partial<T>) => void;
  setTouched: (field: keyof T) => void;
  reset: () => void;
  isValid: boolean;
} {
  const [values, setValuesState] = useState<T>(initialValues);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [touched, setTouchedState] = useState<Record<string, boolean>>({});

  const validateValues = useCallback((vals: T) => {
    if (!validate) return {};
    return validate(vals);
  }, [validate]);

  useEffect(() => {
    const newErrors = validateValues(values);
    setErrors(newErrors);
  }, [values, validateValues]);

  const setValue = useCallback((field: keyof T, value: T[keyof T]) => {
    setValuesState(prev => ({ ...prev, [field]: value }));
  }, []);

  const setValues = useCallback((newValues: Partial<T>) => {
    setValuesState(prev => ({ ...prev, ...newValues }));
  }, []);

  const setTouched = useCallback((field: keyof T) => {
    setTouchedState(prev => ({ ...prev, [field]: true }));
  }, []);

  const reset = useCallback(() => {
    setValuesState(initialValues);
    setErrors({});
    setTouchedState({});
  }, [initialValues]);

  const isValid = Object.keys(errors).length === 0;

  return {
    values,
    errors,
    touched,
    setValue,
    setValues,
    setTouched,
    reset,
    isValid
  };
}

client/src/hooks/use-session-heartbeat.t.content:
import { useEffect, useRef } from 'react';

interface UseSessionHeartbeatOptions {
  enabled: boolean;
  interval?: number; // in milliseconds
  onSessionExpired?: () => void;
}

export function useSessionHeartbeat({ 
  enabled, 
  interval = 5 * 60 * 1000, // 5 minutes default - more frequent to detect issues earlier
  onSessionExpired 
}: UseSessionHeartbeatOptions) {
  const heartbeatRef = useRef<number | null>(null);
  const lastActivityRef = useRef<number>(Date.now());

  useEffect(() => {
    if (!enabled) return;

    // Track user activity
    const updateActivity = () => {
      lastActivityRef.current = Date.now();
    };

    const events = ['mousedown', 'mousemove', 'keypress', 'scroll', 'touchstart', 'click'];
    events.forEach(event => {
      document.addEventListener(event, updateActivity, true);
    });

    // Check session health periodically
    const checkSession = async () => {
      const timeSinceActivity = Date.now() - lastActivityRef.current;

      // Only check if user has been active recently (within 4 hours)
      if (timeSinceActivity > 4 * 60 * 60 * 1000) {
        return;
      }

      try {
        const response = await fetch('/api/auth/me', {
          method: 'GET',
          credentials: 'include',
        });

        if (response.status === 401) {
          onSessionExpired?.();
        }
      } catch (error) {
        // Network error - don't treat as session expiry
        console.warn('Session heartbeat failed due to network error:', error);
      }
    };

    heartbeatRef.current = window.setInterval(checkSession, interval);

    return () => {
      events.forEach(event => {
        document.removeEventListener(event, updateActivity, true);
      });

      if (heartbeatRef.current) {
        clearInterval(heartbeatRef.current);
      }
    };
  }, [enabled, interval, onSessionExpired]);

  // Also check session when page becomes visible (PWA focus)
  useEffect(() => {
    if (!enabled) return;

    const handleVisibilityChange = async () => {
      if (document.visibilityState === 'visible') {
        try {
          const response = await fetch('/api/auth/me', {
            method: 'GET',
            credentials: 'include',
          });

          if (response.status === 401) {
            onSessionExpired?.();
          }
        } catch (error) {
          console.warn('Session check on visibility change failed:', error);
        }
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [enabled, onSessionExpired]);
}

