        variant: "destructive",
      });
      return;
    }

    if (!loginForm.email) {
      toast({
        title: "Error", 
        description: "Please enter your email address",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      // First check if this is a valid pending teacher
      const checkResponse = await fetch('/api/auth/login', {
        method: 'POST',
