        credentials: 'include',
        body: JSON.stringify({
          email: loginForm.email,
          password: 'temp' // This will fail but give us the schoolId if user exists
        })
      });

      const checkData = await checkResponse.json();

      if (!checkData.isFirstLogin) {
        throw new Error('Email not found or account already has a password set. Please contact your administrator.');
      }

      // Now set the password
      const response = await fetch('/api/auth/first-login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ 
          email: loginForm.email, 
          password: firstLoginForm.password,
          schoolId: checkData.schoolId
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to set password');
      }

      // Successful first login - redirect with full page reload
      if (response.ok && data.ok) {
        window.location.replace(data.redirect || '/app'); // Full reload ensures cookie is active
      } else {
        throw new Error(data.message || 'Failed to set password');
      }

    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email: loginForm.email, password: loginForm.password })
      });

      const data = await response.json();

      if (!response.ok) {
        // Check if this is a first-time login scenario
        if (data.isFirstLogin) {
          toast({
            title: "First Time Login",
            description: "Please set your password to continue.",
          });
          setMode('first-login');
          return;
        }
        throw new Error(data.error || 'Login failed');
      }

      if (data.requiresSchool) {
        // User has multiple schools - show school selector
        setRequiresSchool(true);
        setAvailableSchools(data.schools || []);
        toast({
          title: "Multiple Schools Found",
          description: "Please select which school you want to access.",
        });
        return;
      }

      if (response.ok && data.ok) {
        window.location.replace(data.redirect || '/app'); // Full reload ensures cookie is active
      } else {
        throw new Error(data.error || 'Login failed');
      }

    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSchoolLogin = async (schoolId: string) => {
    if (!schoolId) return;

