import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

export default function Login() {
  const { login } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  // Check URL for mode parameter and set initial mode
  const urlParams = new URLSearchParams(window.location.search);
  const urlMode = urlParams.get('mode');
  const initialMode = urlMode === 'forgot-password' ? 'forgot-password' : 'login';

  const [mode, setMode] = useState<'login' | 'register' | 'forgot-password' | 'first-login'>(initialMode);
  const [isLoading, setIsLoading] = useState(false);

  const [loginForm, setLoginForm] = useState({
    email: '',
    password: ''
  });

  // Multi-school login state removed - users go directly to their school

  const [registerForm, setRegisterForm] = useState({
    schoolName: '',
    adminName: '',
    adminEmail: '',
    adminPassword: '',
    plan: 'free'
  });

  const [forgotPasswordForm, setForgotPasswordForm] = useState({
    email: ''
  });

  const [firstLoginForm, setFirstLoginForm] = useState({
    password: '',
    confirmPassword: ''
  });

  // Store first-time login info from backend
  const [firstLoginInfo, setFirstLoginInfo] = useState<{
    email: string;
    schoolId: string;
  } | null>(null);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Read form data directly to avoid "one step behind" issue
      const formElement = e.currentTarget as HTMLFormElement;
      const formData = new FormData(formElement);
      const email = formData.get('email') as string || loginForm.email;
      const password = formData.get('password') as string || loginForm.password;

      console.log("LOGIN - FROM STATE:", loginForm);
      console.log("LOGIN - FROM FORM:", { email, password: '***' });

      // Direct API call to handle multi-school response
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password })
      });

      const data = await response.json();

      // Check if this is a first-time login scenario (even if response is ok)
      if (data.isFirstLogin) {
        setFirstLoginInfo({
          email: data.email,
          schoolId: data.schoolId
        });
        toast({
          title: "First Time Login",
          description: "Please set your password to continue.",
        });
        setMode('first-login');
        return;
      }

      if (!response.ok) {
        throw new Error(data.error || 'Login failed');
      }

      if (data.success) {
        // Single school login success - redirect to app
        setLocation('/');
        toast({
          title: "Welcome!",
          description: "Successfully logged in!",
        });
      }

    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  // handleSchoolLogin function removed - no longer needed

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Read form data directly to avoid "one step behind" issue
      const formElement = e.currentTarget as HTMLFormElement;
      const formData = new FormData(formElement);
      const currentRegisterData = {
        schoolName: formData.get('schoolName') as string || registerForm.schoolName,
        adminName: formData.get('adminName') as string || registerForm.adminName,
        adminEmail: formData.get('adminEmail') as string || registerForm.adminEmail,
        adminPassword: formData.get('adminPassword') as string || registerForm.adminPassword,
        plan: 'free_trial'
      };

      console.log("REGISTER - FROM STATE:", registerForm);
      console.log("REGISTER - FROM FORM:", { ...currentRegisterData, adminPassword: '***' });

      // Register school via API
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(currentRegisterData)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Registration failed');
      }

      // Clear any registration drafts after successful registration
      localStorage.removeItem('passpilot.registrationDraft');
      localStorage.removeItem('pendingRegistration');
      // No need to manually navigate - authentication state will handle routing
      toast({
        title: "Success!",
        description: "School registered successfully!",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleForgotPassword = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('/api/auth/forgot-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: forgotPasswordForm.email })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message);
      }

      toast({
        title: "Reset Email Sent",
        description: "Check your email for password reset instructions.",
      });

      setMode('login');

    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleFirstLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (firstLoginForm.password !== firstLoginForm.confirmPassword) {
      toast({
        title: "Error",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    if (firstLoginForm.password.length < 6) {
      toast({
        title: "Error", 
        description: "Password must be at least 6 characters",
        variant: "destructive",
      });
      return;
    }

    if (!firstLoginInfo?.email) {
      toast({
        title: "Error", 
        description: "Please enter your email address",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      // First, try to find the user to get their school ID
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          email: firstLoginInfo.email,
          password: 'temp' // This will fail but give us the schoolId if user exists
        })
      });

      const data = await response.json();

      if (data.isFirstLogin) {
        // Now set the password
        const setPasswordResponse = await fetch('/api/auth/first-login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          credentials: 'include',
          body: JSON.stringify({
            email: firstLoginInfo.email,
            password: firstLoginForm.password,
            schoolId: data.schoolId
          })
        });

        const setPasswordData = await setPasswordResponse.json();

        if (!setPasswordResponse.ok) {
          throw new Error(setPasswordData.message || 'Failed to set password');
        }

        setLocation('/');
        toast({
          title: "Welcome!",
          description: "Password set successfully! You're now logged in.",
        });
      } else {
        throw new Error('Email not found or account already has a password set');
      }

    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  // School picker feature removed - users go directly to their school

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="w-16 h-16 bg-primary rounded-xl flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-id-card text-white text-2xl"></i>
          </div>
          <CardTitle className="text-2xl font-bold">
            {mode === 'login' ? 'Sign In to PassPilot' : 
             mode === 'forgot-password' ? 'Reset Your Password' : 
             mode === 'first-login' ? 'Set Your Password' :
             'Register Your School'}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {mode === 'first-login' ? (
            <form onSubmit={handleFirstLogin} className="space-y-4">
              <div className="text-sm text-muted-foreground mb-4">
                Enter your email and choose a password for your account.
              </div>

              <div>
                <Label htmlFor="teacherEmail">Email Address</Label>
                <Input
                  type="email"
                  id="teacherEmail"
                  placeholder="teacher@school.edu"
                  value={firstLoginInfo?.email || ''}
                  onChange={(e) => setFirstLoginInfo(prev => prev ? {...prev, email: e.target.value} : {email: e.target.value, schoolId: ''})}
                  required
                  data-testid="input-teacher-email"
                />
              </div>

              <div>
                <Label htmlFor="firstPassword">New Password</Label>
                <Input
                  type="password"
                  id="firstPassword"
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={firstLoginForm.password}
                  onChange={(e) => setFirstLoginForm({ ...firstLoginForm, password: e.target.value })}
                  required
                  minLength={6}
                  data-testid="input-first-password"
                />
              </div>

              <div>
                <Label htmlFor="confirmPassword">Confirm Password</Label>
                <Input
                  type="password"
                  id="confirmPassword"
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={firstLoginForm.confirmPassword}
                  onChange={(e) => setFirstLoginForm({ ...firstLoginForm, confirmPassword: e.target.value })}
                  required
                  minLength={6}
                  data-testid="input-confirm-password"
                />
              </div>

              <Button type="submit" className="w-full" disabled={isLoading} data-testid="button-set-password">
                {isLoading ? "Setting Password..." : "Set Password & Sign In"}
              </Button>

              <Button 
                type="button" 
                variant="ghost" 
                className="w-full" 
                onClick={() => setMode('login')}
                data-testid="button-back-to-login"
              >
                Back to Login
              </Button>
            </form>
          ) : mode === 'login' ? (
            <form onSubmit={handleLogin} className="space-y-4">
              <div>
                <Label htmlFor="email">Email Address</Label>
                <Input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="teacher@school.edu"
                  value={loginForm.email}
                  onChange={(e) => setLoginForm({ ...loginForm, email: e.target.value })}
                  required
                  data-testid="input-email"
                />
              </div>
              <div>
                <Label htmlFor="password">Password</Label>
                <Input
                  type="password"
                  id="password"
                  name="password"
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={loginForm.password}
