import { useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Trash2, AlertTriangle } from "lucide-react";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";

interface ReportsTabProps {
  user: any;
}

export function ReportsTab({ user }: ReportsTabProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [filters, setFilters] = useState({
    dateRange: 'today',
    grade: 'all',
    teacher: 'all',
    passType: 'all',
  });

  const [customDateRange, setCustomDateRange] = useState({
    startDate: '',
    endDate: ''
  });

  // Helper function to calculate duration in minutes
  const calculateDuration = (issuedAt: string, returnedAt?: string) => {
    if (!returnedAt) return null;
    const issued = new Date(issuedAt);
    const returned = new Date(returnedAt);
    const diffMs = returned.getTime() - issued.getTime();
    const diffMinutes = Math.round(diffMs / (1000 * 60));
    // Return actual duration - don't force minimum of 1 minute
    return Math.max(0, diffMinutes);
  };

  const { data: passes = [], refetch, isLoading } = useQuery<any[]>({
    queryKey: ['/api/passes', JSON.stringify(filters), JSON.stringify(customDateRange)], // Ensure deep comparison
    refetchInterval: 3000, // Refresh every 3 seconds
    queryFn: async () => {
      const params = new URLSearchParams();

      // Add cache buster to prevent mobile caching issues
      params.append('t', Date.now().toString());

      if (filters.dateRange && filters.dateRange !== 'all') {
        const now = new Date();
        let dateStart = new Date();

        switch (filters.dateRange) {
          case 'today':
            dateStart.setHours(0, 0, 0, 0);
            params.append('dateStart', dateStart.toISOString());
            break;
          case 'week':
            dateStart.setDate(now.getDate() - 7);
            params.append('dateStart', dateStart.toISOString());
            break;
          case 'month':
            dateStart.setMonth(now.getMonth() - 1);
            params.append('dateStart', dateStart.toISOString());
            break;
          case 'custom':
            if (customDateRange.startDate) {
              params.append('dateStart', new Date(customDateRange.startDate + 'T00:00:00').toISOString());
            }
            if (customDateRange.endDate) {
              params.append('dateEnd', new Date(customDateRange.endDate + 'T23:59:59').toISOString());
            }
            break;
        }
      }

      if (filters.grade && filters.grade !== 'all') {
        params.append('grade', filters.grade);
      }

      if (filters.teacher && filters.teacher !== '' && filters.teacher !== 'all') {
        params.append('teacherId', filters.teacher);
      }

      if (filters.passType && filters.passType !== 'all') {
        params.append('passType', filters.passType);
      }

      const url = `/api/passes${params.toString() ? '?' + params.toString() : ''}`;
      const response = await apiRequest('GET', url);
      const data = await response.json();
      console.log('Reports filter applied:', { filters, customDateRange, resultCount: data?.length, url });
      return Array.isArray(data) ? data : [];
    },
    gcTime: 0, // Don't cache results to ensure fresh data on mobile
  });

  // Auto-refetch when filters change (especially important for mobile PWA)
  useEffect(() => {
    console.log('Filters changed, triggering refetch:', filters);
    refetch();
  }, [filters, customDateRange, refetch]);

  // Delete pass mutation
  const deletePassMutation = useMutation({
    mutationFn: async (passId: string) => {
      const response = await apiRequest('DELETE', `/api/passes/${passId}`);
      if (!response.ok) {
        throw new Error('Failed to delete pass');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/passes'] });
      toast({
        title: "Pass Deleted",
        description: "The pass record has been removed from the system and reports.",
      });
    },
    onError: (error) => {
      console.error('Delete pass error:', error);
      toast({
        title: "Delete Failed",
        description: "Failed to delete the pass record. Please try again.",
        variant: "destructive",
      });
    },
  });

  const { data: grades = [] } = useQuery<any[]>({
    queryKey: ['/api/grades'],
  });

  const { data: teachers = [] } = useQuery<any[]>({
    queryKey: ['/api/teachers'],
  });

  // Filter grades based on teacher's assigned grades
  const teacherAssignedGrades = user?.assignedGrades || [];
  const filteredGrades = teacherAssignedGrades.length === 0 
    ? grades 
    : grades.filter(grade => teacherAssignedGrades.includes(grade.name));

  const handleApplyFilters = () => {
    // Force a fresh fetch by manually calling refetch
    refetch();
    console.log('Manual filter apply clicked:', filters);
    toast({
      title: "Filters Applied",
      description: `Report data updated. Showing results for: ${filters.dateRange}${filters.passType !== 'all' ? `, ${filters.passType} passes` : ''}`,
    });
  };

  const handleExportCSV = () => {
    if (!passes || passes.length === 0) {
      toast({
        title: "No Data",
        description: "No pass data available to export. Apply filters first.",
        variant: "destructive",
      });
      return;
    }

    const csvHeaders = ["Student Name", "Grade/Class", "Teacher", "Pass Type", "Custom Reason", "Checkout Time", "Return Time", "Duration (min)"];
    const csvRows = passes.map((pass: any) => {
      // Check both returnedAt and status to determine if pass is returned
      const isReturned = pass.returnedAt || pass.status === 'returned';
      const calculatedDuration = isReturned ? calculateDuration(pass.issuedAt, pass.returnedAt) : null;
      const displayDuration = calculatedDuration !== null ? calculatedDuration : (pass.duration && pass.duration >= 1 ? pass.duration : null);

      return [
        `${pass.student?.firstName} ${pass.student?.lastName}` || "Unknown",
        pass.student?.grade || "Unknown", 
        `${pass.teacher?.firstName} ${pass.teacher?.lastName}` || "Unknown",
        pass.passType === 'nurse' ? 'Nurse' : 
        pass.passType === 'discipline' ? 'Discipline' : 'General Hall Pass',
        pass.customDestination || pass.customReason || "",
        new Date(pass.issuedAt).toLocaleString(),
        isReturned ? new Date(pass.returnedAt || pass.issuedAt).toLocaleString() : "Still Out",
        displayDuration !== null ? displayDuration : "Still Out"
      ];
    });

    // Add BOM for proper Excel UTF-8 handling and use Excel-compatible format
    const BOM = '\uFEFF';
    const csvContent = BOM + [csvHeaders, ...csvRows]
      .map(row => row.map(field => `"${String(field).replace(/"/g, '""')}"`).join(","))
      .join("\r\n");

    // Use Excel-compatible MIME type for better direct opening
    const blob = new Blob([csvContent], { 
      type: "application/vnd.ms-excel;charset=utf-8;" 
    });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `pass-report-${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);

    toast({
      title: "Export Complete",
      description: "Pass report has been downloaded and should open directly in Excel.",
    });
  };

  const handleExportPDF = async () => {
    if (!passes || passes.length === 0) {
      toast({
        title: "No Data",
        description: "No pass data available to export. Apply filters first.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Dynamic import to avoid bundling issues
      const jsPDF = (await import('jspdf')).default;
      const autoTable = (await import('jspdf-autotable')).default;

      const doc = new jsPDF();

      // Add title
      doc.setFontSize(18);
      doc.text('Hall Pass Report', 14, 22);

      // Add date range and filters info
      doc.setFontSize(12);
      const today = new Date().toLocaleDateString();
      doc.text(`Generated: ${today}`, 14, 35);
      doc.text(`Filter: ${filters.dateRange} | Grade/Class: ${filters.grade} | Teacher: ${filters.teacher}`, 14, 45);

      // Prepare table data
      const tableHeaders = ['Student Name', 'Grade/Class', 'Teacher', 'Pass Type', 'Destination', 'Checkout Time', 'Return Time', 'Duration'];
      const tableRows = passes.map((pass: any) => {
        const isReturned = pass.returnedAt || pass.status === 'returned';
        const calculatedDuration = isReturned ? calculateDuration(pass.issuedAt, pass.returnedAt) : null;
        const displayDuration = calculatedDuration !== null ? `${calculatedDuration} min` : "Still Out";

        return [
          `${pass.student?.firstName} ${pass.student?.lastName}` || "Unknown",
          pass.student?.grade || "Unknown",
          `${pass.teacher?.firstName} ${pass.teacher?.lastName}` || "Unknown",
          pass.passType === 'nurse' ? 'Nurse' : 
          pass.passType === 'discipline' ? 'Discipline' : 'General',
          pass.customDestination || pass.destination || 'General',
          new Date(pass.issuedAt).toLocaleString([], { 
            month: 'short', day: 'numeric', 
            hour: '2-digit', minute: '2-digit' 
          }),
          isReturned ? new Date(pass.returnedAt || pass.issuedAt).toLocaleString([], { 
            month: 'short', day: 'numeric', 
            hour: '2-digit', minute: '2-digit' 
          }) : "Still Out",
          displayDuration
        ];
      });

      // Add table using autoTable plugin
      (doc as any).autoTable({
        head: [tableHeaders],
        body: tableRows,
        startY: 55,
        theme: 'striped',
        headStyles: { fillColor: [66, 139, 202] },
        styles: { fontSize: 8, cellPadding: 2 },
        columnStyles: {
          0: { cellWidth: 25 }, // Student Name
          1: { cellWidth: 15 }, // Grade  
          2: { cellWidth: 25 }, // Teacher
          3: { cellWidth: 20 }, // Pass Type
          4: { cellWidth: 20 }, // Destination
          5: { cellWidth: 25 }, // Checkout Time
          6: { cellWidth: 25 }, // Return Time
          7: { cellWidth: 15 }, // Duration
        },
      });

      // Add summary stats at the bottom
      const finalY = (doc as any).lastAutoTable.finalY + 20;
      doc.setFontSize(12);
      doc.text('Summary Statistics:', 14, finalY);
      doc.setFontSize(10);
      doc.text(`Total Passes: ${stats.totalPasses}`, 14, finalY + 10);
      doc.text(`Average Duration: ${stats.avgDuration} minutes`, 14, finalY + 20);
      doc.text(`Peak Hour: ${stats.peakHour}`, 14, finalY + 30);
      doc.text(`Unique Students: ${stats.uniqueStudents}`, 14, finalY + 40);

      // Save the PDF
      const fileName = `pass-report-${new Date().toISOString().split('T')[0]}.pdf`;
      doc.save(fileName);

      toast({
        title: "PDF Export Complete",
        description: "Pass report has been downloaded as PDF.",
      });
    } catch (error) {
      console.error('PDF Export Error:', error);
      toast({
        title: "Export Failed",
        description: "Failed to generate PDF report. Please try again.",
        variant: "destructive",
      });
    }
  };

  // Calculate real statistics from pass data
  const completedPasses = passes.filter(p => p.status === 'returned' && p.returnedAt);
  const passesWithDuration = completedPasses.map(p => {
    const duration = calculateDuration(p.issuedAt, p.returnedAt) ?? p.duration;
    return { ...p, calculatedDuration: duration };
  }).filter(p => p.calculatedDuration !== null);

  const stats = {
    totalPasses: passes.length,
    avgDuration: passesWithDuration.length > 0 
      ? Math.round(passesWithDuration.reduce((sum, p) => sum + (p.calculatedDuration || 0), 0) / passesWithDuration.length * 10) / 10
      : 0,
    peakHour: passes.length > 0 
      ? new Date(passes.reduce((latest, pass) => 
          new Date(pass.issuedAt) > new Date(latest.issuedAt) ? pass : latest
        ).issuedAt).toLocaleTimeString([], { hour: 'numeric' })
      : 'N/A',
    uniqueStudents: new Set(passes.map(p => p.studentId)).size,
  };

  // Calculate pass type breakdown
  const passTypeStats = {
    general: passes.filter(p => (p.passType || 'general') === 'general').length,
    nurse: passes.filter(p => p.passType === 'nurse').length,
    discipline: passes.filter(p => p.passType === 'discipline').length,
  };

  // Get recent activity from TODAY ONLY (daily reset functionality)
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const todaysPasses = passes.filter(pass => {
    const passDate = new Date(pass.issuedAt);
    return passDate >= today;
  });

  const recentActivity = todaysPasses.length > 0 ? todaysPasses
    .sort((a, b) => new Date(b.issuedAt).getTime() - new Date(a.issuedAt).getTime())
    .slice(0, 10)
    .map(pass => {
      const calculatedDuration = calculateDuration(pass.issuedAt, pass.returnedAt);
      return {
        id: pass.id,
        studentName: `${pass.student?.firstName} ${pass.student?.lastName}` || 'Unknown Student',
        action: pass.status === 'returned' 
          ? `Returned after ${calculatedDuration !== null ? calculatedDuration : (pass.duration || 0)} minutes`
          : `Checked out${pass.customDestination ? ` - ${pass.customDestination}` : (pass.destination ? ` to ${pass.destination}` : '')}`,
        destination: pass.destination,
        time: new Date(pass.issuedAt).toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' }),
        date: 'Today',
        passType: pass.passType || 'general',
        customDestination: pass.customDestination
      };
    }) : [];

  return (
    <div className="p-4">
      <div className="mb-6">
        <h2 className="text-xl font-semibold text-foreground mb-2">Reports</h2>
        <p className="text-sm text-muted-foreground">View and export student pass usage data</p>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <h3 className="font-medium text-foreground mb-4">Filters</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
            <div>
              <Label htmlFor="dateRange">Date Range</Label>
              <Select 
                value={filters.dateRange} 
                onValueChange={(value) => {
                  console.log('Date range changed:', value);
                  setFilters({ ...filters, dateRange: value });
                }}
              >
                <SelectTrigger data-testid="select-date-range">
                  <SelectValue placeholder="Select date range" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="today">Today</SelectItem>
                  <SelectItem value="week">This Week</SelectItem>
                  <SelectItem value="month">This Month</SelectItem>
                  <SelectItem value="custom">Custom Range</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Custom Date Range Inputs */}
            {filters.dateRange === 'custom' && (
              <>
                <div>
                  <Label htmlFor="startDate">Start Date</Label>
                  <Input
                    id="startDate"
                    type="date"
                    value={customDateRange.startDate}
                    onChange={(e) => setCustomDateRange({ ...customDateRange, startDate: e.target.value })}
                    data-testid="input-start-date"
                  />
                </div>
                <div>
                  <Label htmlFor="endDate">End Date</Label>
                  <Input
                    id="endDate"
                    type="date"
                    value={customDateRange.endDate}
                    onChange={(e) => setCustomDateRange({ ...customDateRange, endDate: e.target.value })}
                    data-testid="input-end-date"
                  />
                </div>
              </>
            )}

            <div>
              <Label htmlFor="gradeFilter">Grade/Class</Label>
              <Select 
                value={filters.grade} 
                onValueChange={(value) => {
                  console.log('Grade changed:', value);
                  setFilters({ ...filters, grade: value });
                }}
              >
                <SelectTrigger data-testid="select-grade-filter">
                  <SelectValue placeholder="All Grades" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Grades</SelectItem>
                  {filteredGrades.map((grade: any) => (
                    <SelectItem key={grade.id} value={grade.name}>Grade {grade.name}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="teacherFilter">Teacher</Label>
              <Select 
                value={filters.teacher} 
                onValueChange={(value) => {
                  console.log('Teacher changed:', value);
                  setFilters({ ...filters, teacher: value });
                }}
              >
                <SelectTrigger data-testid="select-teacher-filter">
                  <SelectValue placeholder="All Teachers" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Teachers</SelectItem>
                  {teachers.map((teacher: any) => (
                    <SelectItem key={teacher.id} value={teacher.id}>
                      {teacher.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="passTypeFilter">Pass Type</Label>
              <Select 
                value={filters.passType} 
                onValueChange={(value) => {
                  console.log('Pass type changed:', value);
                  setFilters({ ...filters, passType: value });
                }}
              >
                <SelectTrigger data-testid="select-pass-type-filter">
                  <SelectValue placeholder="All Types" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="general">General</SelectItem>
                  <SelectItem value="nurse">Nurse</SelectItem>
                  <SelectItem value="discipline">Discipline</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-end">
              <Button 
                onClick={handleApplyFilters}
                className="w-full"
                data-testid="button-apply-filters"
                disabled={isLoading}
              >
                {isLoading ? 'Loading...' : 'Apply Filters'}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Pass Type Breakdown */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <h3 className="font-medium text-foreground mb-4">Pass Type Breakdown</h3>
          <div className="grid grid-cols-3 gap-4">
            <div className="text-center">
              <div className="flex items-center justify-center space-x-2 mb-2">
                <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                <span className="text-sm font-medium text-foreground">General</span>
              </div>
              <div className="text-2xl font-bold text-blue-600" data-testid="stat-general-passes">{passTypeStats.general}</div>
              <div className="text-xs text-muted-foreground">Bathroom/General</div>
            </div>
            <div className="text-center">
              <div className="flex items-center justify-center space-x-2 mb-2">
                <div className="w-3 h-3 bg-red-500 rounded-full"></div>
                <span className="text-sm font-medium text-foreground">Nurse</span>
              </div>
              <div className="text-2xl font-bold text-red-600" data-testid="stat-nurse-passes">{passTypeStats.nurse}</div>
              <div className="text-xs text-muted-foreground">Health Office</div>
            </div>
            <div className="text-center">
              <div className="flex items-center justify-center space-x-2 mb-2">
                <div className="w-3 h-3 bg-orange-500 rounded-full"></div>
                <span className="text-sm font-medium text-foreground">Discipline</span>
              </div>
              <div className="text-2xl font-bold text-orange-600" data-testid="stat-discipline-passes">{passTypeStats.discipline}</div>
              <div className="text-xs text-muted-foreground">Office/Admin</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Statistics Cards */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-primary" data-testid="stat-total-passes">{stats.totalPasses}</div>
            <div className="text-sm text-muted-foreground">Total Passes</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-secondary" data-testid="stat-avg-duration">{stats.avgDuration}</div>
            <div className="text-sm text-muted-foreground">Avg Minutes</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-accent" data-testid="stat-peak-hour">{stats.peakHour}</div>
            <div className="text-sm text-muted-foreground">Peak Hour</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-foreground" data-testid="stat-unique-students">{stats.uniqueStudents}</div>
            <div className="text-sm text-muted-foreground">Students</div>
          </CardContent>
        </Card>
      </div>

      {/* Export Options */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <h3 className="font-medium text-foreground">Export Data</h3>
            <div className="flex space-x-2">
              <Button 
                onClick={handleExportCSV}
                className="bg-secondary hover:bg-secondary/90"
                data-testid="button-export-csv"
              >
                <i className="fas fa-file-csv mr-2"></i>Export CSV
              </Button>
              <Button 
                onClick={handleExportPDF}
                className="bg-destructive hover:bg-destructive/90"
                data-testid="button-export-pdf"
              >
                <i className="fas fa-file-pdf mr-2"></i>Export PDF
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Recent Activity */}
      <Card>
        <CardContent className="p-0">
          <div className="p-4 border-b border-border">
            <div className="flex items-center justify-between">
              <h3 className="font-medium text-foreground">Today's Activity</h3>
              <div className="text-xs text-muted-foreground">
                Resets daily ‚Ä¢ View historical data using date filters above
              </div>
            </div>
          </div>
          <div className="divide-y divide-border" data-testid="recent-activity-list">
            {recentActivity.length === 0 ? (
              <div className="p-4 text-center">
                <i className="fas fa-history text-muted-foreground text-2xl mb-2"></i>
                <p className="text-sm text-muted-foreground">No activity today</p>
                <p className="text-xs text-muted-foreground mt-1">
                  Historical data available using date filters above
                </p>
              </div>
            ) : (
              recentActivity.map((activity) => (
                <div key={activity.id} className="p-4" data-testid={`activity-${activity.id}`}>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                        <span className="text-xs font-medium text-primary">
                          {activity.studentName.split(' ').map((n: string) => n[0]).join('')}
                        </span>
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center space-x-2">
                          <p className="text-sm font-medium text-foreground">{activity.studentName}</p>
                          {/* Show destination badge with proper colors */}
                          {(() => {
                            // Get the destination to display
                            const destination = activity.customDestination || activity.destination || '';
                            const lowerDest = destination.toLowerCase();

                            // Determine badge color and text based on destination
                            let badgeClass = 'bg-blue-100 text-blue-700'; // default
                            let displayText = 'General';

                            if (activity.customDestination) {
                              badgeClass = 'bg-purple-100 text-purple-700';
                              displayText = activity.customDestination;
                            } else if (lowerDest.includes('nurse')) {
                              badgeClass = 'bg-red-100 text-red-700';
                              displayText = 'Nurse';
                            } else if (lowerDest.includes('main office') || lowerDest.includes('office')) {
                              badgeClass = 'bg-yellow-100 text-yellow-700';
                              displayText = 'Main Office';
                            } else if (lowerDest.includes('discipline')) {
                              badgeClass = 'bg-orange-100 text-orange-700';
                              displayText = 'Discipline';
                            }

                            return (
                              <span className={`px-2 py-1 text-xs rounded-full ${badgeClass}`}>
                                {displayText}
                              </span>
                            );
                          })()}
                        </div>
                        <p className="text-xs text-muted-foreground">{activity.action}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <div className="text-right">
                        <p className="text-xs text-muted-foreground">{activity.time}</p>
                        <p className="text-xs text-muted-foreground">{activity.date}</p>
                      </div>
                      {/* Delete button with confirmation */}
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-8 w-8 p-0 text-muted-foreground hover:text-destructive"
                            data-testid={`button-delete-pass-${activity.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle className="flex items-center space-x-2">
                              <AlertTriangle className="h-5 w-5 text-destructive" />
                              <span>Delete Pass Record</span>
                            </AlertDialogTitle>
                            <AlertDialogDescription>
                              Are you sure you want to delete this pass record for <strong>{activity.studentName}</strong>? 
                              This will permanently remove it from all reports and cannot be undone.
                              <br /><br />
                              <em>This is useful for correcting mistakes when the wrong student was accidentally marked out.</em>
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => deletePassMutation.mutate(activity.id)}
                              className="bg-destructive hover:bg-destructive/90"
                              disabled={deletePassMutation.isPending}
                            >
                              {deletePassMutation.isPending ? 'Deleting...' : 'Delete Pass'}
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/tabs/roster-tab.ts.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { queryClient } from "@/lib/queryClient";
import { Trash2, Edit, Plus, Users, MousePointer2, Eye } from "lucide-react";

interface RosterTabProps {
  user: any;
  selectedGrades?: Set<string>;
  onGradeClick?: (gradeName: string) => void;
}

export function RosterTab({ user, selectedGrades = new Set(), onGradeClick }: RosterTabProps) {
  const [showAddStudentModal, setShowAddStudentModal] = useState(false);
  const [showBulkAddStudentsModal, setShowBulkAddStudentsModal] = useState(false);
  const [showAddGradeModal, setShowAddGradeModal] = useState(false);
  const [showViewGradeModal, setShowViewGradeModal] = useState(false);
  const [editingStudent, setEditingStudent] = useState<any>(null);
  const [editingGrade, setEditingGrade] = useState<any>(null);
  const [viewingGrade, setViewingGrade] = useState<any>(null);
  const [bulkGrade, setBulkGrade] = useState('');
  const [studentForm, setStudentForm] = useState({
    name: '',
    grade: '',
    studentId: ''
  });
  const [gradeForm, setGradeForm] = useState({
    name: ''
  });
  const [bulkStudentNames, setBulkStudentNames] = useState('');

  const { toast } = useToast();

  const { data: students = [], isLoading: studentsLoading } = useQuery<any[]>({
    queryKey: ['/api/students'],
  });

  const { data: grades = [], isLoading: gradesLoading } = useQuery<any[]>({
    queryKey: ['/api/grades'],
  });

  const isLoading = studentsLoading || gradesLoading;

  const handleAddGrade = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!gradeForm.name) {
      toast({
        title: "Missing Information",
        description: "Please enter a grade name.",
        variant: "destructive",
      });
      return;
    }

    try {
      await apiRequest('POST', '/api/grades', gradeForm);
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });
      setGradeForm({ name: '' });
      setShowAddGradeModal(false);

      toast({
        title: "Grade added",
        description: `Grade ${gradeForm.name} has been added.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleEditGrade = (grade: any) => {
    setEditingGrade(grade);
    setGradeForm({ name: grade.name });
  };

  const handleUpdateGrade = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!gradeForm.name || !editingGrade) {
      toast({
        title: "Missing Information",
        description: "Please enter a grade name.",
        variant: "destructive",
      });
      return;
    }

    try {
      await apiRequest('PUT', `/api/grades/${editingGrade.id}`, gradeForm);
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });
      setGradeForm({ name: '' });
      setEditingGrade(null);

      toast({
        title: "Grade updated",
        description: `Grade has been updated to ${gradeForm.name}.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleDeleteGrade = async (gradeId: string, gradeName: string) => {
    if (!confirm(`Are you sure you want to delete grade "${gradeName}"? This will also delete all students in this grade.`)) {
      return;
    }

    try {
      await apiRequest('DELETE', `/api/grades/${gradeId}`, {});
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });

      toast({
        title: "Grade deleted",
        description: `Grade ${gradeName} has been deleted.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleAddStudent = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!studentForm.name || !studentForm.grade) {
      toast({
        title: "Missing Information",
        description: "Please enter student name and select a grade.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Send in simple format - let backend handle the conversion
      const studentData = {
        name: studentForm.name,
        grade: studentForm.grade,
        studentId: studentForm.studentId || undefined
      };

      await apiRequest('POST', '/api/students', studentData);
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      setStudentForm({ name: '', grade: '', studentId: '' });
      setShowAddStudentModal(false);

      toast({
        title: "Student added",
        description: `${studentForm.name} has been added to grade ${studentForm.grade}.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleBulkAddStudents = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!bulkStudentNames.trim() || !bulkGrade) {
      toast({
        title: "Missing Information",
        description: "Please enter student names and select a grade.",
        variant: "destructive",
      });
      return;
    }

    const lines = bulkStudentNames
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0);

    if (lines.length === 0) {
      toast({
        title: "No students to add",
        description: "Please enter at least one line of student information.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Find the grade ID from the grade name
      const selectedGrade = filteredGrades.find((g: any) => g.name === bulkGrade);
      if (!selectedGrade) {
        toast({
          title: "Invalid Grade",
          description: "Please select a valid grade.",
          variant: "destructive",
        });
        return;
      }

      const promises = lines.map(line => {
        // Extract name from any line format - very flexible approach
        // Remove common prefixes, numbers, special characters, etc.
        let cleanLine = line
          .replace(/^\d+\.\s*/, '') // Remove "1. " numbering
          .replace(/^\d+\)\s*/, '') // Remove "1) " numbering  
          .replace(/^\d+\s*-\s*/, '') // Remove "1 - " numbering
          .replace(/^-\s*/, '') // Remove leading dashes
          .replace(/^\*\s*/, '') // Remove leading asterisks
          .replace(/,.*$/, '') // Remove everything after first comma
          .replace(/\s*\(.*?\)\s*/g, ' ') // Remove anything in parentheses
          .replace(/\s*\[.*?\]\s*/g, ' ') // Remove anything in brackets
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();

        // If after cleaning we still have text, use it as name
        if (!cleanLine) {
          cleanLine = line.trim(); // Fallback to original if cleaning removed everything
        }

        // Send in simple format - let backend handle the conversion
        const studentData = {
          name: cleanLine,
          grade: bulkGrade
        };

        return apiRequest('POST', '/api/students', studentData);
      });

      await Promise.all(promises);
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      setBulkStudentNames('');
      setBulkGrade('');
      setShowBulkAddStudentsModal(false);

      toast({
        title: "Students added",
        description: `${lines.length} student${lines.length !== 1 ? 's' : ''} have been added to grade ${bulkGrade}.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleEditStudent = (student: any) => {
    setEditingStudent(student);
    // Combine firstName and lastName back into a single name field for the form
    const fullName = `${student.firstName || ''} ${student.lastName || ''}`.trim();

    // Find the grade name from gradeId
    const grade = filteredGrades.find((g: any) => g.id === student.gradeId);
    const gradeName = grade ? grade.name : '';

    setStudentForm({
      name: fullName,
      grade: gradeName,
      studentId: student.studentId || ''
    });
  };

  const handleUpdateStudent = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!studentForm.name || !studentForm.grade || !editingStudent) {
      toast({
        title: "Missing Information",
        description: "Please enter student name and select a grade.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Send in simple format - let backend handle the conversion
      const studentData = {
        name: studentForm.name,
        grade: studentForm.grade,
        studentId: studentForm.studentId || undefined
      };

      await apiRequest('PUT', `/api/students/${editingStudent.id}`, studentData);
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      setStudentForm({ name: '', grade: '', studentId: '' });
      setEditingStudent(null);

      toast({
        title: "Student updated",
        description: `${studentForm.name} has been updated.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleDeleteStudent = async (studentId: string, studentName: string) => {
    if (!confirm(`Are you sure you want to delete ${studentName}?`)) {
      return;
    }

    try {
      await apiRequest('DELETE', `/api/students/${studentId}`, {});
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });

      toast({
        title: "Student deleted",
        description: `${studentName} has been deleted.`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const getInitials = (student: any) => {
    const firstName = student.firstName || '';
    const lastName = student.lastName || '';
    return (firstName[0] || '') + (lastName[0] || '').toUpperCase();
  };

  const getAvatarColor = (student: any) => {
    const colors = [
      'bg-blue-100 text-blue-600',
      'bg-pink-100 text-pink-600',
      'bg-green-100 text-green-600',
      'bg-purple-100 text-purple-600',
      'bg-yellow-100 text-yellow-600',
      'bg-red-100 text-red-600'
    ];
    const fullName = (student.firstName || '') + (student.lastName || '');
    const index = fullName.length % colors.length;
    return colors[index];
  };

  // Filter grades based on teacher's assigned grades
  const teacherAssignedGrades = user?.assignedGrades || [];
  const filteredGrades = teacherAssignedGrades.length === 0 
    ? grades 
    : grades.filter(grade => teacherAssignedGrades.includes(grade.name));

  if (isLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-muted rounded w-1/4"></div>
          <div className="h-20 bg-muted rounded"></div>
          <div className="h-20 bg-muted rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <div className="mb-6">
        <h2 className="text-xl font-semibold text-foreground mb-2">Student Roster</h2>
        <p className="text-sm text-muted-foreground">
          Manage grades and students. Click on grade cards to add them to MyClass tab. 
          <span className="text-green-600 font-medium"> Green cards are active in MyClass.</span>
        </p>
      </div>

      {/* Grades Section */}
      <Card className="mb-6">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <Users className="w-5 h-5" />
              Classes
            </CardTitle>
            <Dialog open={showAddGradeModal} onOpenChange={setShowAddGradeModal}>
              <DialogTrigger asChild>
                <Button size="sm" data-testid="button-add-grade">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Grade/Class
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Add New Grade</DialogTitle>
                </DialogHeader>
                <form onSubmit={handleAddGrade} className="space-y-4">
                  <div>
                    <Label htmlFor="gradeName">Grade Name</Label>
                    <Input
                      id="gradeName"
                      value={gradeForm.name}
                      onChange={(e) => setGradeForm({ name: e.target.value })}
                      placeholder="e.g. 6th, 7th, 8th"
                      data-testid="input-grade-name"
                    />
                  </div>
                  <div className="flex justify-end space-x-2">
                    <Button type="button" variant="outline" onClick={() => setShowAddGradeModal(false)}>
                      Cancel
                    </Button>
                    <Button type="submit" data-testid="button-submit-grade">Add Grade/Class</Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent>
          {filteredGrades.length === 0 ? (
            <p className="text-center text-muted-foreground py-4">
              {teacherAssignedGrades.length === 0 
                ? "No grades created yet. Add a grade to get started."
                : "No assigned grades found. Contact your admin to assign grades to your account."
              }
            </p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {filteredGrades.map((grade: any) => {
                const gradeStudents = students.filter(student => student.gradeId === grade.id);
                const isSelected = selectedGrades.has(grade.name);

                return (
                  <Card 
                    key={grade.id} 
                    className={`cursor-pointer hover:shadow-md transition-all border-2 ${
                      isSelected 
                        ? 'border-green-500 bg-green-50 shadow-md' 
                        : 'hover:border-primary/20'
                    }`}
                    onClick={() => onGradeClick?.(grade.name)}
                    data-testid={`card-grade-${grade.name}`}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          <MousePointer2 className={`w-4 h-4 ${isSelected ? 'text-green-600' : 'text-primary'}`} />
                          <h3 className={`font-semibold ${isSelected ? 'text-green-700' : ''}`}>Grade {grade.name}</h3>
                          {isSelected && (
                            <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                          )}
                        </div>
                        <div className="flex items-center space-x-1">
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={(e) => {
                              e.stopPropagation();
                              setViewingGrade(grade);
                              setShowViewGradeModal(true);
                            }}
                            className="h-6 w-6 p-0"
                            data-testid={`button-view-grade-${grade.id}`}
                          >
                            <Eye className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEditGrade(grade);
                            }}
                            className="h-6 w-6 p-0"
                            data-testid={`button-edit-grade-${grade.id}`}
                          >
                            <Edit className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleDeleteGrade(grade.id, grade.name);
                            }}
                            className="h-6 w-6 p-0 hover:text-red-600"
                            data-testid={`button-delete-grade-${grade.id}`}
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </div>
                      </div>
                      <p className={`text-sm ${isSelected ? 'text-green-600' : 'text-muted-foreground'}`}>
                        {gradeStudents.length} student{gradeStudents.length !== 1 ? 's' : ''}
                      </p>
                      <div className="flex justify-between items-center mt-1">
                        <p className={`text-xs ${isSelected ? 'text-green-600' : 'text-primary'}`}>
                          {isSelected ? '‚úì Active in MyClass' : 'Click to add to MyClass ‚Üí'}
                        </p>
                        <div className="flex gap-1">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              setStudentForm({ name: '', grade: grade.name, studentId: '' });
                              setShowAddStudentModal(true);
                            }}
                            className="h-6 text-xs px-2"
                            data-testid={`button-add-student-${grade.name}`}
                          >
                            Add Student
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              setBulkGrade(grade.name);
                              setShowBulkAddStudentsModal(true);
                            }}
                            className="h-6 text-xs px-2"
                            data-testid={`button-bulk-add-${grade.name}`}
                          >
                            Bulk Add
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>



      {/* Add Student Dialog */}
      <Dialog open={showAddStudentModal} onOpenChange={setShowAddStudentModal}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add New Student</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleAddStudent} className="space-y-4">
            <div>
              <Label htmlFor="studentName">Student Name</Label>
              <Input
                id="studentName"
                value={studentForm.name}
                onChange={(e) => setStudentForm({ ...studentForm, name: e.target.value })}
                placeholder="Enter student name"
                data-testid="input-student-name"
              />
            </div>
            <div>
              <Label htmlFor="studentGrade">Grade</Label>
              <Select 
                value={studentForm.grade} 
                onValueChange={(value) => setStudentForm({ ...studentForm, grade: value })}
              >
                <SelectTrigger data-testid="select-student-grade">
                  <SelectValue placeholder="Select a grade" />
                </SelectTrigger>
                <SelectContent>
                  {filteredGrades.map((grade: any) => (
                    <SelectItem key={grade.id} value={grade.name}>
                      Grade {grade.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="studentId">Student ID (Optional)</Label>
              <Input
                id="studentId"
                value={studentForm.studentId}
                onChange={(e) => setStudentForm({ ...studentForm, studentId: e.target.value })}
                placeholder="Enter student ID"
                data-testid="input-student-id"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <Button type="button" variant="outline" onClick={() => setShowAddStudentModal(false)}>
                Cancel
              </Button>
              <Button type="submit" data-testid="button-submit-student">Add Student</Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Bulk Add Students Dialog */}
      <Dialog open={showBulkAddStudentsModal} onOpenChange={setShowBulkAddStudentsModal}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Bulk Add Students</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleBulkAddStudents} className="space-y-4">
            <div>
              <Label htmlFor="bulkGrade">Grade</Label>
              <Select 
                value={bulkGrade} 
                onValueChange={setBulkGrade}
              >
                <SelectTrigger data-testid="select-bulk-grade">
                  <SelectValue placeholder="Select a grade" />
                </SelectTrigger>
                <SelectContent>
                  {filteredGrades.map((grade: any) => (
                    <SelectItem key={grade.id} value={grade.name}>
                      Grade {grade.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="bulkStudentNames">Student Information (one per line)</Label>
              <textarea
                id="bulkStudentNames"
                value={bulkStudentNames}
                onChange={(e) => setBulkStudentNames(e.target.value)}
                placeholder="Paste any student information, one per line:&#10;John Smith&#10;1. Jane Doe (Student ID: 12345)&#10;Alex Johnson, Grade 6&#10;- Sarah Wilson&#10;* Mike Davis"
                className="w-full h-32 p-3 border rounded-md resize-none"
                data-testid="textarea-bulk-student-names"
              />
              <p className="text-sm text-muted-foreground mt-1">
                Paste any student information - I'll extract the names automatically. Works with lists, numbers, commas, etc.
              </p>
            </div>
            <div className="flex justify-end space-x-2">
              <Button type="button" variant="outline" onClick={() => setShowBulkAddStudentsModal(false)}>
                Cancel
              </Button>
              <Button type="submit" data-testid="button-submit-bulk-students">Add Students</Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Edit Grade Dialog */}
      {editingGrade && (
        <Dialog open={!!editingGrade} onOpenChange={() => setEditingGrade(null)}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Edit Grade</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleUpdateGrade} className="space-y-4">
              <div>
                <Label htmlFor="editGradeName">Grade Name</Label>
                <Input
                  id="editGradeName"
                  value={gradeForm.name}
                  onChange={(e) => setGradeForm({ name: e.target.value })}
                  placeholder="e.g. 6th, 7th, 8th"
                  data-testid="input-edit-grade-name"
                />
              </div>
              <div className="flex justify-end space-x-2">
                <Button type="button" variant="outline" onClick={() => setEditingGrade(null)}>
                  Cancel
                </Button>
                <Button type="submit" data-testid="button-update-grade">Update Grade</Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      )}

      {/* Edit Student Dialog */}
      {editingStudent && (
        <Dialog open={!!editingStudent} onOpenChange={() => setEditingStudent(null)}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Edit Student</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleUpdateStudent} className="space-y-4">
              <div>
                <Label htmlFor="editStudentName">Student Name</Label>
                <Input
                  id="editStudentName"
                  value={studentForm.name}
                  onChange={(e) => setStudentForm({ ...studentForm, name: e.target.value })}
                  placeholder="Enter student name"
                  data-testid="input-edit-student-name"
                />
              </div>
              <div>
                <Label htmlFor="editStudentGrade">Grade</Label>
                <Select 
                  value={studentForm.grade} 
                  onValueChange={(value) => setStudentForm({ ...studentForm, grade: value })}
                >
                  <SelectTrigger data-testid="select-edit-student-grade">
                    <SelectValue placeholder="Select a grade" />
                  </SelectTrigger>
                  <SelectContent>
                    {filteredGrades.map((grade: any) => (
                      <SelectItem key={grade.id} value={grade.name}>
                        Grade {grade.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="editStudentId">Student ID (Optional)</Label>
                <Input
                  id="editStudentId"
                  value={studentForm.studentId}
                  onChange={(e) => setStudentForm({ ...studentForm, studentId: e.target.value })}
                  placeholder="Enter student ID"
                  data-testid="input-edit-student-id"
                />
              </div>
              <div className="flex justify-end space-x-2">
                <Button type="button" variant="outline" onClick={() => setEditingStudent(null)}>
                  Cancel
                </Button>
                <Button type="submit" data-testid="button-update-student">Update Student</Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      )}

      {/* View Grade Roster Dialog */}
      {viewingGrade && (
        <Dialog open={showViewGradeModal} onOpenChange={setShowViewGradeModal}>
          <DialogContent className="max-w-4xl">
            <DialogHeader>
              <DialogTitle>Grade {viewingGrade.name} - Full Roster</DialogTitle>
            </DialogHeader>
            <div className="max-h-96 overflow-y-auto">
              {(() => {
                const gradeStudents = students.filter(student => student.gradeId === viewingGrade.id);

                if (gradeStudents.length === 0) {
                  return (
                    <div className="text-center py-8">
                      <Users className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                      <p className="text-muted-foreground">No students in Grade {viewingGrade.name} yet.</p>
                      <div className="flex gap-2 justify-center mt-4">
                        <Button
                          onClick={() => {
                            setStudentForm({ name: '', grade: viewingGrade.name, studentId: '' });
                            setShowViewGradeModal(false);
                            setShowAddStudentModal(true);
                          }}
                          size="sm"
                        >
                          Add Student
                        </Button>
                        <Button
                          onClick={() => {
                            setBulkGrade(viewingGrade.name);
                            setShowViewGradeModal(false);
                            setShowBulkAddStudentsModal(true);
                          }}
                          size="sm"
                          variant="outline"
                        >
                          Bulk Add Students
                        </Button>
                      </div>
                    </div>
                  );
                }

                return (
                  <div className="space-y-3">
                    <div className="flex justify-between items-center">
                      <p className="text-sm text-muted-foreground">
                        {gradeStudents.length} student{gradeStudents.length !== 1 ? 's' : ''} in Grade {viewingGrade.name}
                      </p>
                      <div className="flex gap-2">
                        <Button
                          onClick={() => {
                            setStudentForm({ name: '', grade: viewingGrade.name, studentId: '' });
                            setShowViewGradeModal(false);
                            setShowAddStudentModal(true);
                          }}
                          size="sm"
                        >
                          <Plus className="w-4 h-4 mr-2" />
                          Add Student
                        </Button>
                        <Button
                          onClick={() => {
                            setBulkGrade(viewingGrade.name);
                            setShowViewGradeModal(false);
                            setShowBulkAddStudentsModal(true);
                          }}
                          size="sm"
                          variant="outline"
                        >
                          <Users className="w-4 h-4 mr-2" />
                          Bulk Add
                        </Button>
                      </div>
                    </div>
                    {gradeStudents.map((student: any) => (
                      <div key={student.id} className="flex items-center justify-between p-3 bg-muted/30 rounded-lg">
                        <div className="flex items-center space-x-3">
                          <div className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-medium ${getAvatarColor(student)}`}>
                            {getInitials(student)}
                          </div>
                          <div>
                            <p className="font-medium">{student.firstName} {student.lastName}</p>
                            {student.studentId && (
                              <p className="text-sm text-muted-foreground">ID: {student.studentId}</p>
                            )}
                          </div>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => {
                              handleEditStudent(student);
                              setShowViewGradeModal(false);
                            }}
                            data-testid={`button-edit-student-${student.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => handleDeleteStudent(student.id, `${student.firstName} ${student.lastName}`)}
                            className="hover:text-red-600"
                            data-testid={`button-delete-student-${student.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                );
              })()}
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/components/tabs/setup-tab.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Trash2, UserPlus, UserMinus, Users, Mail, Crown, AlertTriangle, Edit, Key, Settings } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { queryClient } from "@/lib/queryClient";
import { FirebaseStatus } from "@/components/firebase-status";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

interface SetupTabProps {
  user: any;
}

export function SetupTab({ user }: SetupTabProps) {
  const [settings, setSettings] = useState({
    schoolName: user.schoolName || '',
    enableNotifications: true,
    autoReturn: false,
    passTimeout: 30,
  });
  const [assignedGrades, setAssignedGrades] = useState<string[]>(user.assignedGrades || []);
  const [profileForm, setProfileForm] = useState({
    firstName: user.firstName || '',
    lastName: user.lastName || '',
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [newTeacherEmail, setNewTeacherEmail] = useState('');
  const [newTeacherName, setNewTeacherName] = useState('');
  const [isAddingTeacher, setIsAddingTeacher] = useState(false);

  // Edit teacher state
  const [editingTeacher, setEditingTeacher] = useState<any>(null);
  const [editTeacherForm, setEditTeacherForm] = useState({
    firstName: '',
    lastName: '',
    email: ''
  });

  // Password reset state
  const [resettingPasswordFor, setResettingPasswordFor] = useState<string | null>(null);
  const [newPassword, setNewPassword] = useState('');
  const { toast } = useToast();

  const { data: grades = [] } = useQuery<any[]>({
    queryKey: ['/api/grades'],
  });

  const { data: teachers = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/teachers'],
    enabled: user.isAdmin,
  });

  const { data: schoolInfo } = useQuery<any>({
    queryKey: ['/api/admin/school-info'],
    enabled: user.isAdmin,
  });

  const handleSaveSettings = async () => {
    try {
      await apiRequest('PUT', '/api/users/settings', {
        ...settings,
        assignedGrades,
      });

      queryClient.invalidateQueries({ queryKey: ['/api/users/me'] });

      toast({
        title: "Settings Saved",
        description: "Your settings have been updated successfully.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleGradeAssignment = (gradeName: string, checked: boolean) => {
    if (checked) {
      setAssignedGrades([...assignedGrades, gradeName]);
    } else {
      setAssignedGrades(assignedGrades.filter(g => g !== gradeName));
    }
  };

  const addTeacherMutation = useMutation({
    mutationFn: async (teacherData: { email: string; name: string }) => {
      const response = await apiRequest('POST', '/api/admin/teachers', teacherData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/teachers'] });
      setNewTeacherEmail('');
      setNewTeacherName('');
      setIsAddingTeacher(false);
      toast({
        title: "Teacher Added",
        description: "Teacher invitation sent. They'll set their password on first login.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to add teacher",
        variant: "destructive",
      });
    },
  });

  const removeTeacherMutation = useMutation({
    mutationFn: async (teacherId: string) => {
      await apiRequest('DELETE', `/api/admin/teachers/${teacherId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/teachers'] });
      toast({
        title: "Teacher Removed",
        description: "Teacher has been removed from your school.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to remove teacher",
        variant: "destructive",
      });
    },
  });

  const promoteTeacherMutation = useMutation({
    mutationFn: async (teacherId: string) => {
      const response = await apiRequest('PATCH', `/api/admin/teachers/${teacherId}/promote`);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/teachers'] });
      toast({
        title: "Teacher Promoted",
        description: data.message || "Teacher has been promoted to admin.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to promote teacher",
        variant: "destructive",
      });
    },
  });

  const demoteAdminMutation = useMutation({
    mutationFn: async (teacherId: string) => {
      const response = await apiRequest('PATCH', `/api/admin/teachers/${teacherId}/demote`);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/teachers'] });
      toast({
        title: "Admin Demoted",
        description: data.message || "Admin has been demoted to teacher.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to demote admin",
        variant: "destructive",
      });
    },
  });

  const editTeacherMutation = useMutation({
    mutationFn: async ({ teacherId, updates }: { teacherId: string; updates: any }) => {
      const response = await apiRequest('PATCH', `/api/admin/teachers/${teacherId}`, updates);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/teachers'] });
      setEditingTeacher(null);
      toast({
        title: "Teacher Updated",
        description: "Teacher information has been updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update teacher",
        variant: "destructive",
      });
    },
  });

  const resetPasswordMutation = useMutation({
    mutationFn: async ({ teacherId, password }: { teacherId: string; password: string }) => {
      const response = await apiRequest('POST', `/api/admin/teachers/${teacherId}/reset-password`, { password });
      return response.json();
    },
    onSuccess: () => {
      setResettingPasswordFor(null);
      setNewPassword('');
      toast({
        title: "Password Reset",
        description: "Teacher's password has been reset successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reset password",
        variant: "destructive",
      });
    },
  });

  const updateProfileMutation = useMutation({
    mutationFn: async (profileData: any) => {
      const response = await apiRequest('PATCH', '/api/profile', profileData);
      return response.json();
    },
    onSuccess: (data) => {
      setProfileForm(prev => ({
        ...prev,
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      }));
      // Update user context if available
      if (data.user) {
        queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });
      }
      toast({
        title: "Profile Updated",
        description: data.message || "Your profile has been updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  const handleAddTeacher = () => {
    if (!newTeacherEmail || !newTeacherName) {
      toast({
        title: "Error",
        description: "Please enter email and name",
        variant: "destructive",
      });
      return;
    }

    const maxTeachers = schoolInfo?.maxTeachers || 1;
    if (teachers.length >= maxTeachers) {
      toast({
        title: "User Limit Reached",
        description: `Your ${schoolInfo?.plan || 'current'} plan allows ${maxTeachers} teacher${maxTeachers === 1 ? '' : 's'}. Upgrade to add more teachers.`,
        variant: "destructive",
      });
      return;
    }

    addTeacherMutation.mutate({ 
      email: newTeacherEmail, 
      name: newTeacherName
    });
  };

  const handleEditTeacher = (teacher: any) => {
    setEditingTeacher(teacher);
    setEditTeacherForm({
      firstName: teacher.firstName || '',
      lastName: teacher.lastName || '',
      email: teacher.email || ''
    });
  };

  const handleSaveTeacherEdit = () => {
    if (!editingTeacher) return;

    editTeacherMutation.mutate({
      teacherId: editingTeacher.id,
      updates: editTeacherForm
    });
  };

  const handleResetPassword = (teacherId: string) => {
    if (!newPassword || newPassword.length < 6) {
      toast({
        title: "Error",
        description: "Password must be at least 6 characters long",
        variant: "destructive",
      });
      return;
    }

    resetPasswordMutation.mutate({ teacherId, password: newPassword });
  };

  const handleUpdateProfile = () => {
    // Validate name changes
    const nameChanged = profileForm.firstName !== user.firstName || profileForm.lastName !== user.lastName;
    const passwordChanged = profileForm.newPassword.length > 0;

    if (!nameChanged && !passwordChanged) {
      toast({
        title: "No Changes",
        description: "Please make changes to update your profile",
        variant: "destructive",
      });
      return;
    }

    // Validate password if changing
    if (passwordChanged) {
      if (!profileForm.currentPassword) {
        toast({
          title: "Current Password Required",
          description: "Please enter your current password to change it",
          variant: "destructive",
        });
        return;
      }

      if (profileForm.newPassword.length < 6) {
        toast({
          title: "Password Too Short",
          description: "New password must be at least 6 characters long",
          variant: "destructive",
        });
        return;
      }

      if (profileForm.newPassword !== profileForm.confirmPassword) {
        toast({
          title: "Passwords Don't Match",
          description: "New password and confirmation must match",
          variant: "destructive",
        });
        return;
      }
    }

    // Validate name if changing
    if (nameChanged && (!profileForm.firstName.trim() || !profileForm.lastName.trim())) {
      toast({
        title: "Name Required",
        description: "Please enter both first and last name",
        variant: "destructive",
      });
      return;
    }

    // Prepare update data
    const updateData: any = {};

    if (nameChanged) {
      updateData.firstName = profileForm.firstName.trim();
      updateData.lastName = profileForm.lastName.trim();
    }

    if (passwordChanged) {
      updateData.currentPassword = profileForm.currentPassword;
      updateData.newPassword = profileForm.newPassword;
    }

    updateProfileMutation.mutate(updateData);
  };

  const getMaxTeachersForPlan = (plan: string) => {
    switch (plan) {
      case 'free': return 10;
      case 'basic_10': return 10;
      case 'standard_25': return 25;
      case 'premium_50': return 50;
      case 'enterprise_unlimited': return 'Unlimited';
      default: return 10;
    }
  };

  return (
    <div className="p-4">
      <div className="mb-6">
        <h2 className="text-xl font-semibold text-foreground mb-2">Admin Panel</h2>
        <p className="text-sm text-muted-foreground">Configure school settings and system administration</p>
      </div>

      <div className="space-y-6">
        {/* Firebase Integration Status */}
        <FirebaseStatus />

        {/* Teacher Management - Admin Only */}
        {user.isAdmin && (
          <>
            {/* School Plan & User Limits */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Crown className="w-5 h-5" />
                  School Plan & Billing
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <Label>Current Plan</Label>
                    <div className="flex items-center gap-2 mt-1">
                      <Badge variant="outline">
                        {schoolInfo?.plan?.replace('_', ' ').toUpperCase() || 'FREE'}
                      </Badge>
                      {schoolInfo?.plan && schoolInfo.plan !== 'trial' && schoolInfo.plan !== 'free' && (
                        <Button 
                          variant="link" 
                          size="sm" 
                          className="text-red-600 hover:text-red-700 p-0 h-auto"
                          onClick={() => window.open('mailto:support@passpilotsystem.com?subject=Cancellation Request&body=I would like to cancel my subscription for school: ' + encodeURIComponent(schoolInfo?.schoolName || ''), '_blank')}
                          data-testid="link-cancel-subscription"
                        >
                          Cancel Subscription
                        </Button>
                      )}
                    </div>
                  </div>
                  <div>
                    <Label>Teachers</Label>
                    <p className="text-sm text-muted-foreground mt-1">
                      {teachers.length} / {getMaxTeachersForPlan(schoolInfo?.plan || 'free')}
                    </p>
                  </div>
                  <div>
                    <Label>Support</Label>
                    <div className="mt-1 space-y-1">
                      <p className="text-xs text-muted-foreground">
                        Need help? Contact support
                      </p>
                      <Button 
                        variant="link" 
                        size="sm" 
                        className="text-blue-600 hover:text-blue-700 p-0 h-auto text-xs"
                        onClick={() => window.open('mailto:support@passpilotsystem.com?subject=Support Request&body=School: ' + encodeURIComponent(schoolInfo?.schoolName || '') + '%0ASchool ID: ' + encodeURIComponent(schoolInfo?.schoolId || ''), '_blank')}
                        data-testid="link-contact-support"
                      >
                        support@passpilotsystem.com
                      </Button>
                    </div>
                  </div>
                </div>

                {/* Billing Actions for Paid Plans */}
                {schoolInfo?.plan && schoolInfo.plan !== 'trial' && schoolInfo.plan !== 'free' && (
                  <div className="mt-4 p-4 bg-red-50 dark:bg-red-900/20 rounded-lg border border-red-200 dark:border-red-800">
                    <h4 className="text-sm font-medium text-red-800 dark:text-red-200 mb-2">
                      Subscription Management
                    </h4>
                    <p className="text-xs text-red-700 dark:text-red-300 mb-3">
                      Need to cancel your subscription? Contact our support team for assistance with plan changes or cancellations.
                    </p>
                    <div className="flex flex-wrap gap-2">
                      <Button 
                        variant="outline" 
                        size="sm"
                        className="text-red-600 border-red-300 hover:bg-red-50 hover:text-red-700"
                        onClick={() => window.open('mailto:support@passpilotsystem.com?subject=Cancellation Request&body=School: ' + encodeURIComponent(schoolInfo?.schoolName || '') + '%0ASchool ID: ' + encodeURIComponent(schoolInfo?.schoolId || '') + '%0APlan: ' + encodeURIComponent(schoolInfo?.plan || '') + '%0A%0AI would like to cancel my subscription.', '_blank')}
                        data-testid="button-cancel-subscription"
                      >
                        Cancel Subscription
                      </Button>
                      <Button 
                        variant="outline" 
                        size="sm"
                        className="text-blue-600 border-blue-300 hover:bg-blue-50 hover:text-blue-700"
                        onClick={() => window.open('mailto:support@passpilotsystem.com?subject=Plan Change Request&body=School: ' + encodeURIComponent(schoolInfo?.schoolName || '') + '%0ASchool ID: ' + encodeURIComponent(schoolInfo?.schoolId || '') + '%0ACurrent Plan: ' + encodeURIComponent(schoolInfo?.plan || '') + '%0A%0AI would like to change my plan.', '_blank')}
                        data-testid="button-change-plan"
                      >
                        Change Plan
                      </Button>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Add Teacher */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <UserPlus className="w-5 h-5" />
                  Add New Teacher
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {!isAddingTeacher ? (
                    <Button 
                      onClick={() => setIsAddingTeacher(true)}
                      className="w-full"
                      data-testid="button-add-teacher"
                    >
                      <UserPlus className="w-4 h-4 mr-2" />
                      Add Teacher
                    </Button>
                  ) : (
                    <div className="space-y-3">
                      <div>
                        <Label htmlFor="teacher-email">Teacher Email</Label>
                        <Input
                          id="teacher-email"
                          type="email"
                          value={newTeacherEmail}
                          onChange={(e) => setNewTeacherEmail(e.target.value)}
                          placeholder="teacher@school.edu"
                          data-testid="input-teacher-email"
                        />
                      </div>
                      <div>
                        <Label htmlFor="teacher-name">Teacher Name</Label>
                        <Input
                          id="teacher-name"
                          value={newTeacherName}
                          onChange={(e) => setNewTeacherName(e.target.value)}
                          placeholder="Teacher Name"
                          data-testid="input-teacher-name"
                        />
                      </div>

                      <div className="flex gap-2">
                        <Button 
                          onClick={handleAddTeacher}
                          disabled={addTeacherMutation.isPending}
                          data-testid="button-confirm-add-teacher"
                        >
                          {addTeacherMutation.isPending ? 'Adding...' : 'Add Teacher'}
                        </Button>
                        <Button 
                          variant="outline" 
                          onClick={() => setIsAddingTeacher(false)}
                          data-testid="button-cancel-add-teacher"
                        >
                          Cancel
                        </Button>
                      </div>
                    </div>
                  )}

                  {schoolInfo?.maxTeachers && teachers.length >= schoolInfo.maxTeachers && (
                    <div className="mt-4 p-3 bg-amber-50 dark:bg-amber-900/20 rounded-lg flex items-center gap-2">
                      <AlertTriangle className="w-4 h-4 text-amber-600" />
                      <p className="text-sm text-amber-700 dark:text-amber-300">
                        You've reached your plan limit of {schoolInfo.maxTeachers} teacher{schoolInfo.maxTeachers === 1 ? '' : 's'}. 
                        Upgrade your plan to add more teachers.
                      </p>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Current Teachers */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Users className="w-5 h-5" />
                  Current Teachers ({teachers.length})
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {teachers.length === 0 ? (
                    <p className="text-sm text-muted-foreground text-center py-4">
                      No teachers added yet. Add your first teacher above.
                    </p>
                  ) : (
                    teachers.map((teacher: any) => (
                      <div 
                        key={teacher.id} 
                        className="flex items-center justify-between p-3 border rounded-lg"
                        data-testid={`teacher-item-${teacher.email}`}
                      >
                        <div className="flex items-center gap-3">
                          <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
                            <Mail className="w-4 h-4 text-primary" />
                          </div>
                          <div>
                            <p className="font-medium">{teacher.name}</p>
                            <p className="text-sm text-muted-foreground">{teacher.email}</p>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          <Badge variant={teacher.status === 'active' ? 'default' : 'secondary'}>
                            {teacher.status}
                          </Badge>
                          <Badge variant={teacher.isAdmin ? 'default' : 'outline'}>
                            {teacher.isAdmin ? 'Admin' : 'Teacher'}
                          </Badge>

                          {/* Edit Teacher Button */}
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => handleEditTeacher(teacher)}
                            data-testid={`button-edit-teacher-${teacher.email}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>

                          {/* Reset Password Button */}
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => setResettingPasswordFor(teacher.id)}
                            data-testid={`button-reset-password-${teacher.email}`}
                          >
                            <Key className="w-4 h-4" />
                          </Button>

                          {/* Admin Management Actions - Allow management of all users except yourself when you're the only admin */}
                          {(teacher.id !== user?.id || teachers.filter((t: any) => t.isAdmin).length > 1) && (
                            <>
                              {teacher.isAdmin ? (
                                <AlertDialog>
                                  <AlertDialogTrigger asChild>
                                    <Button 
                                      variant="ghost" 
                                      size="sm"
                                      className="text-orange-600 hover:text-orange-700"
                                      data-testid={`button-demote-admin-${teacher.email}`}
                                    >
                                      <UserMinus className="w-4 h-4" />
                                    </Button>
                                  </AlertDialogTrigger>
                                  <AlertDialogContent>
                                    <AlertDialogHeader>
                                      <AlertDialogTitle>Demote Admin</AlertDialogTitle>
                                      <AlertDialogDescription>
                                        Are you sure you want to demote {teacher.name} ({teacher.email}) from admin to teacher? 
                                        They will lose admin privileges and cannot access admin functions.
                                      </AlertDialogDescription>
                                    </AlertDialogHeader>
                                    <AlertDialogFooter>
                                      <AlertDialogCancel>Cancel</AlertDialogCancel>
                                      <AlertDialogAction 
                                        onClick={() => demoteAdminMutation.mutate(teacher.id)}
                                        className="bg-orange-600 text-white hover:bg-orange-700"
                                      >
                                        Demote to Teacher
                                      </AlertDialogAction>
                                    </AlertDialogFooter>
                                  </AlertDialogContent>
                                </AlertDialog>
                              ) : (
                                <AlertDialog>
                                  <AlertDialogTrigger asChild>
                                    <Button 
                                      variant="ghost" 
                                      size="sm"
                                      className="text-green-600 hover:text-green-700"
                                      data-testid={`button-promote-teacher-${teacher.email}`}
                                    >
                                      <UserPlus className="w-4 h-4" />
                                    </Button>
                                  </AlertDialogTrigger>
                                  <AlertDialogContent>
                                    <AlertDialogHeader>
                                      <AlertDialogTitle>Promote to Admin</AlertDialogTitle>
                                      <AlertDialogDescription>
                                        Are you sure you want to promote {teacher.name} ({teacher.email}) to admin? 
                                        They will gain full administrative privileges including the ability to manage other teachers and school settings.
                                      </AlertDialogDescription>
                                    </AlertDialogHeader>
                                    <AlertDialogFooter>
                                      <AlertDialogCancel>Cancel</AlertDialogCancel>
                                      <AlertDialogAction 
                                        onClick={() => promoteTeacherMutation.mutate(teacher.id)}
                                        className="bg-green-600 text-white hover:bg-green-700"
                                      >
                                        Promote to Admin
                                      </AlertDialogAction>
                                    </AlertDialogFooter>
                                  </AlertDialogContent>
                                </AlertDialog>
                              )}

                              {/* Remove Teacher/Admin - Allow removal if not the last admin */}
                              {(!teacher.isAdmin || teachers.filter((t: any) => t.isAdmin).length > 1) && (
                                <AlertDialog>
                                  <AlertDialogTrigger asChild>
                                    <Button 
                                      variant="ghost" 
                                      size="sm"
                                      className="text-destructive hover:text-destructive"
                                      data-testid={`button-remove-teacher-${teacher.email}`}
                                    >
                                      <Trash2 className="w-4 h-4" />
                                    </Button>
                                  </AlertDialogTrigger>
                                  <AlertDialogContent>
                                    <AlertDialogHeader>
                                      <AlertDialogTitle>Remove {teacher.isAdmin ? 'Admin' : 'Teacher'}</AlertDialogTitle>
                                      <AlertDialogDescription>
                                        Are you sure you want to remove {teacher.name} ({teacher.email}) from your school? 
                                        This action cannot be undone and will revoke their access to all school data.
                                        {teacher.isAdmin && ' They will lose all administrative privileges.'}
                                      </AlertDialogDescription>
                                    </AlertDialogHeader>
                                    <AlertDialogFooter>
                                      <AlertDialogCancel>Cancel</AlertDialogCancel>
                                      <AlertDialogAction 
                                        onClick={() => removeTeacherMutation.mutate(teacher.id)}
                                        className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                      >
                                        Remove {teacher.isAdmin ? 'Admin' : 'Teacher'}
                                      </AlertDialogAction>
                                    </AlertDialogFooter>
                                  </AlertDialogContent>
                                </AlertDialog>
                              )}
                            </>
                          )}
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </>
        )}

        <Card>
          <CardHeader>
            <CardTitle>School Information</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="schoolName">School Name</Label>
                <Input
                  id="schoolName"
                  value={settings.schoolName}
                  onChange={(e) => setSettings({ ...settings, schoolName: e.target.value })}
                  data-testid="input-school-name"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Teacher Profile */}
        <Card>
          <CardHeader>
            <CardTitle>Teacher Profile</CardTitle>
            <p className="text-sm text-muted-foreground">Update your personal information and password</p>
          </CardHeader>
          <CardContent>
            <div className="space-y-6">
              {/* Edit Name Section */}
              <div>
                <h4 className="text-sm font-medium mb-3">Personal Information</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="editFirstName">First Name</Label>
                    <Input
                      id="editFirstName"
                      value={profileForm.firstName}
                      onChange={(e) => setProfileForm(prev => ({ ...prev, firstName: e.target.value }))}
                      placeholder="Enter first name"
                      data-testid="input-edit-first-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="editLastName">Last Name</Label>
                    <Input
                      id="editLastName"
                      value={profileForm.lastName}
                      onChange={(e) => setProfileForm(prev => ({ ...prev, lastName: e.target.value }))}
                      placeholder="Enter last name"
                      data-testid="input-edit-last-name"
                    />
                  </div>
                </div>
                <div className="mt-4">
                  <Label htmlFor="teacherEmail">Email (Read Only)</Label>
                  <Input
                    id="teacherEmail"
                    value={user.email || ''}
                    readOnly
                    className="bg-gray-50 dark:bg-gray-800"
                    data-testid="input-teacher-email"
                  />
                </div>
              </div>

              {/* Change Password Section */}
              <div>
                <h4 className="text-sm font-medium mb-3">Change Password</h4>
                <div className="space-y-3">
                  <div>
                    <Label htmlFor="currentPassword">Current Password</Label>
                    <Input
                      id="currentPassword"
                      type="password"
                      value={profileForm.currentPassword}
                      onChange={(e) => setProfileForm(prev => ({ ...prev, currentPassword: e.target.value }))}
                      placeholder="Enter current password"
                      data-testid="input-current-password"
                    />
                  </div>
                  <div>
                    <Label htmlFor="newPassword">New Password</Label>
                    <Input
                      id="newPassword"
                      type="password"
                      value={profileForm.newPassword}
                      onChange={(e) => setProfileForm(prev => ({ ...prev, newPassword: e.target.value }))}
                      placeholder="Enter new password (min 6 characters)"
                      data-testid="input-new-password"
                    />
                  </div>
                  <div>
                    <Label htmlFor="confirmPassword">Confirm New Password</Label>
                    <Input
                      id="confirmPassword"
                      type="password"
                      value={profileForm.confirmPassword}
                      onChange={(e) => setProfileForm(prev => ({ ...prev, confirmPassword: e.target.value }))}
                      placeholder="Confirm new password"
                      data-testid="input-confirm-password"
                    />
                  </div>
                </div>
              </div>

              {/* Read-only School Info */}
              <div>
                <h4 className="text-sm font-medium mb-3">School Information</h4>
                <div>
                  <Label htmlFor="schoolName">School</Label>
                  <Input
                    id="schoolName"
                    value={user.schoolName || ''}
                    readOnly
                    className="bg-gray-50 dark:bg-gray-800"
                    data-testid="input-school-name-readonly"
                  />
                </div>
              </div>

              {/* Save Profile Button */}
              <Button 
                onClick={handleUpdateProfile}
                disabled={updateProfileMutation.isPending}
                className="w-full"
                data-testid="button-update-profile"
              >
                {updateProfileMutation.isPending ? 'Updating...' : 'Update Profile'}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Grade Assignment for Teachers */}
        {!user.isAdmin && (
          <Card>
            <CardHeader>
              <CardTitle>Grade Level Access</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <p className="text-sm text-muted-foreground">
                  Select which grade levels you want access to. You can manage students and passes for these grades.
                </p>
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {grades.map((grade: any) => (
                    <div key={grade.id} className="flex items-center space-x-3 p-3 border rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800">
                      <Checkbox
                        id={`grade-${grade.id}`}
                        checked={assignedGrades.includes(grade.name)}
                        onCheckedChange={(checked) => handleGradeAssignment(grade.name, !!checked)}
                        data-testid={`checkbox-grade-${grade.name}`}
                      />
                      <Label htmlFor={`grade-${grade.id}`} className="text-sm font-medium cursor-pointer">
                        Grade {grade.name}
                      </Label>
                    </div>
                  ))}
                </div>
                {grades.length === 0 && (
                  <p className="text-sm text-muted-foreground bg-gray-50 dark:bg-gray-800 p-3 rounded">
                    No grades available yet. Add grades in the Roster tab first.
                  </p>
                )}
                {assignedGrades.length === 0 && grades.length > 0 && (
                  <p className="text-sm text-amber-600 bg-amber-50 dark:bg-amber-900/20 p-3 rounded">
                    Please select at least one grade to access student management features.
                  </p>
                )}
                {assignedGrades.length > 0 && (
                  <div className="mt-4 p-3 bg-green-50 dark:bg-green-900/20 rounded">
                    <p className="text-sm text-green-700 dark:text-green-300 font-medium">
                      You have access to {assignedGrades.length} grade level{assignedGrades.length !== 1 ? 's' : ''}: {assignedGrades.join(', ')}
                    </p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        <Button 
          onClick={handleSaveSettings}
          className="w-full"
          data-testid="button-save-settings"
        >
          Save Settings
        </Button>

        {/* Edit Teacher Dialog */}
        {editingTeacher && (
          <Dialog open={!!editingTeacher} onOpenChange={() => setEditingTeacher(null)}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Edit Teacher</DialogTitle>
                <DialogDescription>
                  Update teacher information for {editingTeacher.firstName} {editingTeacher.lastName}
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="edit-firstName">First Name</Label>
                  <Input
                    id="edit-firstName"
                    value={editTeacherForm.firstName}
                    onChange={(e) => setEditTeacherForm({ ...editTeacherForm, firstName: e.target.value })}
                    data-testid="input-edit-first-name"
                  />
                </div>
                <div>
                  <Label htmlFor="edit-lastName">Last Name</Label>
                  <Input
                    id="edit-lastName"
                    value={editTeacherForm.lastName}
                    onChange={(e) => setEditTeacherForm({ ...editTeacherForm, lastName: e.target.value })}
                    data-testid="input-edit-last-name"
                  />
                </div>
                <div>
                  <Label htmlFor="edit-email">Email</Label>
                  <Input
                    id="edit-email"
                    type="email"
                    value={editTeacherForm.email}
                    onChange={(e) => setEditTeacherForm({ ...editTeacherForm, email: e.target.value })}
                    data-testid="input-edit-email"
                  />
                </div>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => setEditingTeacher(null)}>
                  Cancel
                </Button>
                <Button 
                  onClick={handleSaveTeacherEdit}
                  disabled={editTeacherMutation.isPending}
                  data-testid="button-save-teacher-edit"
                >
                  {editTeacherMutation.isPending ? 'Saving...' : 'Save Changes'}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        )}

        {/* Reset Password Dialog */}
        {resettingPasswordFor && (
          <Dialog open={!!resettingPasswordFor} onOpenChange={() => setResettingPasswordFor(null)}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Reset Password</DialogTitle>
                <DialogDescription>
                  Set a new password for this teacher. They will be able to use this password to log in.
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="new-password">New Password</Label>
                  <Input
                    id="new-password"
                    type="password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    placeholder="Enter new password (min 6 characters)"
                    data-testid="input-new-password"
                  />
                </div>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => {
                  setResettingPasswordFor(null);
                  setNewPassword('');
                }}>
                  Cancel
                </Button>
                <Button 
                  onClick={() => handleResetPassword(resettingPasswordFor)}
                  disabled={resetPasswordMutation.isPending}
                  data-testid="button-confirm-reset-password"
                >
                  {resetPasswordMutation.isPending ? 'Resetting...' : 'Reset Password'}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        )}
      </div>
    </div>
  );
}

client/src/components/tabs/upload-tab.ts.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { queryClient } from "@/lib/queryClient";

interface UploadTabProps {
  user: any;
}

export function UploadTab({ user }: UploadTabProps) {
  const [isUploading, setIsUploading] = useState(false);
  const [cleverConnected, setCleverConnected] = useState(false);
  const [googleConnected, setGoogleConnected] = useState(false);
  const { toast } = useToast();

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.name.endsWith('.csv')) {
      toast({
        title: "Invalid File Type",
        description: "Please upload a CSV file.",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/upload/csv', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();

      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });

      toast({
        title: "Upload Complete",
        description: `Successfully imported ${result.studentsAdded} students and ${result.gradesAdded} grades.`,
      });
    } catch (error: any) {
      toast({
        title: "Upload Failed",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleCleverImport = async () => {
    setIsUploading(true);
    try {
      const response = await apiRequest('POST', '/api/import/clever', {});
      const result = await response.json();

      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });

      toast({
        title: "Clever Import Complete",
        description: `Successfully imported ${result.studentsAdded} students from Clever.`,
      });
      setCleverConnected(true);
    } catch (error: any) {
      toast({
        title: "Clever Import Failed",
        description: "Please check your Clever API credentials and try again.",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleGoogleClassroomImport = async () => {
    setIsUploading(true);
    try {
      const response = await apiRequest('POST', '/api/import/google-classroom', {});
      const result = await response.json();

      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });

      toast({
        title: "Google Classroom Import Complete",
        description: `Successfully imported ${result.studentsAdded} students from Google Classroom.`,
      });
      setGoogleConnected(true);
    } catch (error: any) {
      toast({
        title: "Google Classroom Import Failed",
        description: "Please check your Google Classroom API credentials and try again.",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="p-4">
      <div className="mb-6">
        <h2 className="text-xl font-semibold text-foreground mb-2">Upload Student Data</h2>
        <p className="text-sm text-muted-foreground">Import student rosters from multiple sources</p>
      </div>

      <div className="space-y-6">
        {/* CSV Upload */}
        <Card>
          <CardHeader>
            <CardTitle>CSV File Upload</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="fileUpload">Select CSV File</Label>
                <Input
                  id="fileUpload"
                  type="file"
                  accept=".csv"
                  onChange={handleFileUpload}
                  disabled={isUploading}
                  data-testid="input-file-upload"
                />
              </div>

              <div className="text-sm text-muted-foreground">
                <p className="mb-2">CSV file should contain the following columns:</p>
                <ul className="list-disc list-inside space-y-1">
                  <li>name (required)</li>
                  <li>grade (required)</li>
                  <li>studentId (optional)</li>
                </ul>
              </div>

              {isUploading && (
                <div className="flex items-center space-x-2">
                  <div className="animate-spin w-4 h-4 border-2 border-primary border-t-transparent rounded-full"></div>
                  <span className="text-sm text-muted-foreground">Processing upload...</span>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Clever Integration */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              Clever Integration
              {cleverConnected && (
                <span className="text-sm text-green-600 bg-green-100 px-2 py-1 rounded">Connected</span>
              )}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <p className="text-sm text-muted-foreground">
                Import student rosters directly from your Clever account. This will sync all students from your connected schools.
              </p>
              <Button
                onClick={handleCleverImport}
                disabled={isUploading}
                className="w-full"
                data-testid="button-clever-import"
              >
                {isUploading ? "Importing..." : "Import from Clever"}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Google Classroom Integration */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              Google Classroom Integration
              {googleConnected && (
                <span className="text-sm text-green-600 bg-green-100 px-2 py-1 rounded">Connected</span>
              )}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <p className="text-sm text-muted-foreground">
                Import student rosters from your Google Classroom courses. All enrolled students will be added to your roster.
              </p>
              <Button
                onClick={handleGoogleClassroomImport}
                disabled={isUploading}
                className="w-full"
                data-testid="button-google-classroom-import"
              >
                {isUploading ? "Importing..." : "Import from Google Classroom"}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

